function getPayment(array $array) 
{
    if ($array['paid']) 
        return [
            'date' => \Carbon\Carbon::parse($array['date']),
            'amount' => (float) str_replace(',', '.', $array['paid']),
        ]; 
    else return [];
}

function getDismissalDate($users, $username)
{
    $key = $users->search(function($user) use ($username) {
