function noise($text) {
  $map = [
    'А' => ['A', 'Å', 'Ä'],
    'а' => ['a', 'å', 'ä'],
    'О' => ['O', 'Ό'],
    'о' => ['o', 'ọ'],
    'Е' => ['E', 'Ė'],
    'е' => ['e', '℮'],
    'В' => ['B', 'ᗷ'],
    'Ж' => ['Җ'],
    'ж' => ['җ'],
    'К' => ['K'],
    'к' => ['k', 'ҝ'],
    'Т' => ['T'],
    'Ф' => ['Փ'],
    'Н' => ['H'],
    'у' => ['y'],
  ];
  
  $result = '';
  for ($i = 0; $i < mb_strlen($text); $i++) {
    $word = mb_substr($text, $i, 1);
    $result .= isset($map[$word]) ? (rand(0, 1) ? $map[$word][array_rand($map[$word])] : $word) : $word;
  }
  return $result;
}

