    'desc' => $item['desc'], 
    'debt' => $item['debt'],
    'debtAmount' => (float) str_replace(',', '.', $item['debtAmount']),
    'paidAmount' => (float) str_replace(',', '.', $item['paidAmount']),
    'dateAmount' => $item['dateAmount'],
    'debtNumber' => $item['debtNumber'],
    'documentNumber' => $item['documentNumber'],
    'documentType' => $item['documentType'],
    'id' => $item['documentNumber'] . '-' . $item['desc'],
  ];
})->groupBy('id')->map(function($item, $key){
  $debtAmount = $item->reduce(function($accum, $el){
    return $el['debtAmount'] + $accum;
  }, 0);
  $paidAmount = $item->reduce(function($accum, $el){
    return $el['paidAmount'] + $accum;
  }, 0);
  $saldo = $debtAmount - $paidAmount;
  if (abs($saldo) < 0.01) $saldo = 0;
  return [
    'name' => $item[0]['name'],
    'debtDate' => $item[0]['debtDate'],
    'desc' => $item[0]['desc'], 
    'debt' => $item[0]['debt'],
    'debtAmount' => $debtAmount,
    'paidAmount' => $paidAmount,
    'paids' => $item->map(function($el){
      return [
        'date' => $el['dateAmount'],
        'amount' => $el['paidAmount'],
        'documentType' => $el['documentType'],
        'documentNumber' => $el['documentNumber'],
      ];
    }),
    'debtNumber' => $item[0]['debtNumber'],
    'id' => $item[0]['id'],
    'saldo' => $saldo,
  ];
});

function typeDocument($type)
{
  switch($type) {
    case '01': return 'п/п';
    case '06': return 'и/п';
    case '16': return 'п/о';
    default: return 'б/о';
  }
}

